export default function CollapsibleTable() {
  const [bahan,setBahan] = useState([]);
  useEffect(()=>{
    Axios.get("http://localhost:3005/api/getListBahan").then((response)=>{
      // console.log(response.data);
      setBahan(response.data);
    //   rows = response.data;
